import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

export default function Payment() {
    const [step, setStep] = useState(1);
    const [currencies, setCurrencies] = useState([]);
    const [providers, setProviders] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");
    const [success, setSuccess] = useState("");

    const [paymentData, setPaymentData] = useState({
        amount: "",
        currency: "",
        provider: "",
        beneficiaryAccountNumber: "",
        beneficiaryName: "",
        beneficiaryBankName: "",
        swiftCode: "",
        beneficiaryAddress: ""
    });

    // Load supported currencies and providers on component mount
    useEffect(() => {
        const loadData = async () => {
            try {
                const [currenciesRes, providersRes] = await Promise.all([
                    fetch("http://localhost:4000/api/payments/currencies/supported"),
                    fetch("http://localhost:4000/api/payments/providers/supported")
                ]);

                const currenciesData = await currenciesRes.json();
                const providersData = await providersRes.json();

                setCurrencies(currenciesData.currencies);
                setProviders(providersData.providers);
            } catch (error) {
                console.error("Error loading data:", error);
                setError("Failed to load payment options");
            }
        };

        loadData();
    }, []);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setPaymentData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const validateStep1 = () => {
        if (!paymentData.amount || paymentData.amount <= 0) {
            setError("Please enter a valid amount");
            return false;
        }
        if (!paymentData.currency) {
            setError("Please select a currency");
            return false;
        }
        if (!paymentData.provider) {
            setError("Please select a payment provider");
            return false;
        }
        return true;
    };

    const validateStep2 = () => {
        if (!paymentData.beneficiaryAccountNumber) {
            setError("Please enter beneficiary account number");
            return false;
        }
        if (!paymentData.beneficiaryName) {
            setError("Please enter beneficiary name");
            return false;
        }
        if (!paymentData.beneficiaryBankName) {
            setError("Please enter beneficiary bank name");
            return false;
        }
        if (!paymentData.swiftCode) {
            setError("Please enter SWIFT code");
            return false;
        }
        if (!paymentData.beneficiaryAddress) {
            setError("Please enter beneficiary address");
            return false;
        }
        return true;
    };

    const handleNext = () => {
        setError("");
        if (step === 1 && validateStep1()) {
            setStep(2);
        }
    };

    const handleBack = () => {
        setError("");
        setStep(1);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        setError("");
        setSuccess("");

        if (!validateStep2()) {
            return;
        }

        setLoading(true);

        try {
            // Get token from localStorage (assuming user is logged in)
            const token = localStorage.getItem('token');
            if (!token) {
                setError("Please log in to make a payment");
                setLoading(false);
                return;
            }

            const response = await fetch("http://localhost:4000/api/payments/create", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${token}`
                },
                body: JSON.stringify(paymentData)
            });

            const data = await response.json();

            if (response.ok) {
                setSuccess(`Payment initiated successfully! Transaction ID: ${data.transactionId}`);
                // Reset form
                setPaymentData({
                    amount: "",
                    currency: "",
                    provider: "",
                    beneficiaryAccountNumber: "",
                    beneficiaryName: "",
                    beneficiaryBankName: "",
                    swiftCode: "",
                    beneficiaryAddress: ""
                });
                setStep(1);
            } else {
                setError(data.message || "Payment failed. Please try again.");
            }
        } catch (error) {
            console.error("Payment error:", error);
            setError("Network error. Please check your connection and try again.");
        } finally {
            setLoading(false);
        }
    };

    const formatAmount = (amount) => {
        const selectedCurrency = currencies.find(c => c.code === paymentData.currency);
        if (selectedCurrency) {
            return `${selectedCurrency.symbol}${parseFloat(amount).toLocaleString()}`;
        }
        return amount;
    };

    return (
        <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px' }}>
            <h2>International Payment</h2>
            
            {error && (
                <div style={{ color: 'red', marginBottom: '10px', padding: '10px', backgroundColor: '#ffe6e6', border: '1px solid #ff9999', borderRadius: '4px' }}>
                    {error}
                </div>
            )}

            {success && (
                <div style={{ color: 'green', marginBottom: '10px', padding: '10px', backgroundColor: '#e6ffe6', border: '1px solid #99ff99', borderRadius: '4px' }}>
                    {success}
                </div>
            )}

            <div style={{ marginBottom: '20px' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
                    <div style={{ 
                        padding: '10px', 
                        backgroundColor: step >= 1 ? '#4CAF50' : '#f0f0f0', 
                        color: step >= 1 ? 'white' : 'black',
                        borderRadius: '50%',
                        width: '30px',
                        height: '30px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center'
                    }}>
                        1
                    </div>
                    <div style={{ 
                        padding: '10px', 
                        backgroundColor: step >= 2 ? '#4CAF50' : '#f0f0f0', 
                        color: step >= 2 ? 'white' : 'black',
                        borderRadius: '50%',
                        width: '30px',
                        height: '30px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center'
                    }}>
                        2
                    </div>
                </div>
                <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '12px' }}>
                    <span>Payment Details</span>
                    <span>Account Information</span>
                </div>
            </div>

            <form onSubmit={handleSubmit}>
                {step === 1 && (
                    <div>
                        <h3>Step 1: Payment Details</h3>
                        
                        <div style={{ marginBottom: '15px' }}>
                            <label>Amount *</label>
                            <input
                                type="number"
                                name="amount"
                                value={paymentData.amount}
                                onChange={handleChange}
                                placeholder="Enter amount"
                                min="0.01"
                                step="0.01"
                                required
                                style={{ width: '100%', padding: '10px', marginTop: '5px' }}
                            />
                        </div>

                        <div style={{ marginBottom: '15px' }}>
                            <label>Currency *</label>
                            <select
                                name="currency"
                                value={paymentData.currency}
                                onChange={handleChange}
                                required
                                style={{ width: '100%', padding: '10px', marginTop: '5px' }}
                            >
                                <option value="">Select Currency</option>
                                {currencies.map(currency => (
                                    <option key={currency.code} value={currency.code}>
                                        {currency.code} - {currency.name}
                                    </option>
                                ))}
                            </select>
                        </div>

                        <div style={{ marginBottom: '15px' }}>
                            <label>Payment Provider *</label>
                            <select
                                name="provider"
                                value={paymentData.provider}
                                onChange={handleChange}
                                required
                                style={{ width: '100%', padding: '10px', marginTop: '5px' }}
                            >
                                <option value="">Select Provider</option>
                                {providers.map(provider => (
                                    <option key={provider.code} value={provider.code}>
                                        {provider.name}
                                    </option>
                                ))}
                            </select>
                        </div>

                        {paymentData.provider && (
                            <div style={{ 
                                padding: '10px', 
                                backgroundColor: '#f9f9f9', 
                                border: '1px solid #ddd', 
                                borderRadius: '4px',
                                marginBottom: '15px'
                            }}>
                                <strong>Provider Info:</strong>
                                {providers.find(p => p.code === paymentData.provider)?.description}
                                <br />
                                <small>Fees: {providers.find(p => p.code === paymentData.provider)?.fees}</small>
                            </div>
                        )}

                        <button
                            type="button"
                            onClick={handleNext}
                            style={{
                                backgroundColor: '#4CAF50',
                                color: 'white',
                                padding: '12px 24px',
                                border: 'none',
                                borderRadius: '4px',
                                cursor: 'pointer',
                                fontSize: '16px'
                            }}
                        >
                            Next
                        </button>
                    </div>
                )}

                {step === 2 && (
                    <div>
                        <h3>Step 2: Account Information</h3>
                        
                        <div style={{ 
                            padding: '15px', 
                            backgroundColor: '#e8f5e8', 
                            border: '1px solid #4CAF50', 
                            borderRadius: '4px',
                            marginBottom: '20px'
                        }}>
                            <strong>Payment Summary:</strong><br />
                            Amount: {formatAmount(paymentData.amount)}<br />
                            Currency: {paymentData.currency}<br />
                            Provider: {paymentData.provider}
                        </div>

                        <div style={{ marginBottom: '15px' }}>
                            <label>Beneficiary Account Number *</label>
                            <input
                                type="text"
                                name="beneficiaryAccountNumber"
                                value={paymentData.beneficiaryAccountNumber}
                                onChange={handleChange}
                                placeholder="Enter account number"
                                required
                                style={{ width: '100%', padding: '10px', marginTop: '5px' }}
                            />
                        </div>

                        <div style={{ marginBottom: '15px' }}>
                            <label>Beneficiary Name *</label>
                            <input
                                type="text"
                                name="beneficiaryName"
                                value={paymentData.beneficiaryName}
                                onChange={handleChange}
                                placeholder="Enter beneficiary full name"
                                required
                                style={{ width: '100%', padding: '10px', marginTop: '5px' }}
                            />
                        </div>

                        <div style={{ marginBottom: '15px' }}>
                            <label>Beneficiary Bank Name *</label>
                            <input
                                type="text"
                                name="beneficiaryBankName"
                                value={paymentData.beneficiaryBankName}
                                onChange={handleChange}
                                placeholder="Enter bank name"
                                required
                                style={{ width: '100%', padding: '10px', marginTop: '5px' }}
                            />
                        </div>

                        <div style={{ marginBottom: '15px' }}>
                            <label>SWIFT Code *</label>
                            <input
                                type="text"
                                name="swiftCode"
                                value={paymentData.swiftCode}
                                onChange={handleChange}
                                placeholder="Enter SWIFT code (e.g., CHASUS33)"
                                required
                                style={{ width: '100%', padding: '10px', marginTop: '5px' }}
                                maxLength="11"
                            />
                            <small style={{ color: '#666' }}>
                                Format: 8 or 11 characters (e.g., CHASUS33 or CHASUS33XXX)
                            </small>
                        </div>

                        <div style={{ marginBottom: '15px' }}>
                            <label>Beneficiary Address *</label>
                            <textarea
                                name="beneficiaryAddress"
                                value={paymentData.beneficiaryAddress}
                                onChange={handleChange}
                                placeholder="Enter beneficiary address"
                                required
                                rows="3"
                                style={{ width: '100%', padding: '10px', marginTop: '5px' }}
                            />
                        </div>

                        <div style={{ display: 'flex', gap: '10px' }}>
                            <button
                                type="button"
                                onClick={handleBack}
                                style={{
                                    backgroundColor: '#6c757d',
                                    color: 'white',
                                    padding: '12px 24px',
                                    border: 'none',
                                    borderRadius: '4px',
                                    cursor: 'pointer',
                                    fontSize: '16px'
                                }}
                            >
                                Back
                            </button>
                            
                            <button
                                type="submit"
                                disabled={loading}
                                style={{
                                    backgroundColor: loading ? '#cccccc' : '#4CAF50',
                                    color: 'white',
                                    padding: '12px 24px',
                                    border: 'none',
                                    borderRadius: '4px',
                                    cursor: loading ? 'not-allowed' : 'pointer',
                                    fontSize: '16px'
                                }}
                            >
                                {loading ? 'Processing...' : 'Pay Now'}
                            </button>
                        </div>
                    </div>
                )}
            </form>

            <div style={{ marginTop: '20px', textAlign: 'center' }}>
                <Link to="/" style={{ color: '#4CAF50', textDecoration: 'none' }}>
                    ← Back to Home
                </Link>
            </div>
        </div>
    );
}
