import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

export default function TransactionHistory() {
    const [transactions, setTransactions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
        const loadTransactions = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError("Please log in to view transactions");
                    setLoading(false);
                    return;
                }

                const response = await fetch("http://localhost:4000/api/payments/my-payments", {
                    headers: {
                        "Authorization": `Bearer ${token}`
                    }
                });

                const data = await response.json();

                if (response.ok) {
                    setTransactions(data.payments);
                } else {
                    setError(data.message || "Failed to load transactions");
                }
            } catch (error) {
                console.error("Error loading transactions:", error);
                setError("Network error. Please try again.");
            } finally {
                setLoading(false);
            }
        };

        loadTransactions();
    }, []);

    const getStatusColor = (status) => {
        switch (status) {
            case 'completed': return '#4CAF50';
            case 'processing': return '#FF9800';
            case 'pending': return '#2196F3';
            case 'failed': return '#f44336';
            case 'cancelled': return '#9E9E9E';
            default: return '#666';
        }
    };

    const formatDate = (dateString) => {
        return new Date(dateString).toLocaleString();
    };

    const formatAmount = (amount, currency) => {
        return `${currency} ${parseFloat(amount).toLocaleString()}`;
    };

    if (loading) {
        return (
            <div style={{ padding: '20px', textAlign: 'center' }}>
                <h2>Transaction History</h2>
                <p>Loading transactions...</p>
            </div>
        );
    }

    return (
        <div style={{ maxWidth: '800px', margin: '0 auto', padding: '20px' }}>
            <h2>Transaction History</h2>
            
            {error && (
                <div style={{ color: 'red', marginBottom: '10px', padding: '10px', backgroundColor: '#ffe6e6', border: '1px solid #ff9999', borderRadius: '4px' }}>
                    {error}
                </div>
            )}

            {transactions.length === 0 ? (
                <div style={{ textAlign: 'center', padding: '40px' }}>
                    <h3>No transactions found</h3>
                    <p>You haven't made any payments yet.</p>
                    <Link to="/payment" style={{ 
                        display: 'inline-block', 
                        padding: '10px 20px', 
                        backgroundColor: '#4CAF50', 
                        color: 'white', 
                        textDecoration: 'none', 
                        borderRadius: '4px',
                        marginTop: '10px'
                    }}>
                        Make Your First Payment
                    </Link>
                </div>
            ) : (
                <div>
                    <p>Total transactions: {transactions.length}</p>
                    
                    <div style={{ overflowX: 'auto' }}>
                        <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
                            <thead>
                                <tr style={{ backgroundColor: '#f5f5f5' }}>
                                    <th style={{ padding: '12px', textAlign: 'left', border: '1px solid #ddd' }}>Transaction ID</th>
                                    <th style={{ padding: '12px', textAlign: 'left', border: '1px solid #ddd' }}>Amount</th>
                                    <th style={{ padding: '12px', textAlign: 'left', border: '1px solid #ddd' }}>Provider</th>
                                    <th style={{ padding: '12px', textAlign: 'left', border: '1px solid #ddd' }}>Status</th>
                                    <th style={{ padding: '12px', textAlign: 'left', border: '1px solid #ddd' }}>Date</th>
                                </tr>
                            </thead>
                            <tbody>
                                {transactions.map((transaction) => (
                                    <tr key={transaction._id}>
                                        <td style={{ padding: '12px', border: '1px solid #ddd' }}>
                                            <code style={{ fontSize: '12px' }}>{transaction.transactionId}</code>
                                        </td>
                                        <td style={{ padding: '12px', border: '1px solid #ddd' }}>
                                            <strong>{formatAmount(transaction.amount, transaction.currency)}</strong>
                                        </td>
                                        <td style={{ padding: '12px', border: '1px solid #ddd' }}>
                                            {transaction.provider}
                                        </td>
                                        <td style={{ padding: '12px', border: '1px solid #ddd' }}>
                                            <span style={{ 
                                                padding: '4px 8px', 
                                                borderRadius: '4px', 
                                                color: 'white', 
                                                backgroundColor: getStatusColor(transaction.status),
                                                fontSize: '12px',
                                                textTransform: 'capitalize'
                                            }}>
                                                {transaction.status}
                                            </span>
                                        </td>
                                        <td style={{ padding: '12px', border: '1px solid #ddd' }}>
                                            {formatDate(transaction.createdAt)}
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            )}

            <div style={{ marginTop: '20px', textAlign: 'center' }}>
                <Link to="/" style={{ color: '#4CAF50', textDecoration: 'none', marginRight: '20px' }}>
                    ← Back to Home
                </Link>
                <Link to="/payment" style={{ 
                    display: 'inline-block', 
                    padding: '10px 20px', 
                    backgroundColor: '#4CAF50', 
                    color: 'white', 
                    textDecoration: 'none', 
                    borderRadius: '4px'
                }}>
                    Make New Payment
                </Link>
            </div>
        </div>
    );
}
